mapper.select:
SELECT
-- 级联表的字段 ,sheyingshi.sheyingshi_name as sheyingshiName ,sheyingshi.sheyingshi_phone as sheyingshiPhone ,sheyingshi.sheyingshi_id_number as sheyingshiIdNumber ,sheyingshi.sex_types as sexTypes ,sheyingshi.age as age ,sheyingshi.fengge_types as fenggeTypes ,sheyingshi.sheyingshi_xingji_types as sheyingshiXingjiTypes ,sheyingshi.sheyingshi_photo as sheyingshiPhoto ,sheyingshi.sheyingshi_content as sheyingshiContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto
FROM sheyingshi_order a left JOIN sheyingshi sheyingshi ON a.sheyingshi_id = sheyingshi.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.sheyingshi_id = #{params.sheyingshiId}
and a.yonghu_id = #{params.yonghuId}
and a.sheyingshi_order_types = #{params.sheyingshiOrderTypes}
and UNIX_TIMESTAMP(a.sheyingshi_order_start_time) >= UNIX_TIMESTAMP(#{params.sheyingshiOrderStartTimeStart})
and UNIX_TIMESTAMP(a.sheyingshi_order_start_time) <= UNIX_TIMESTAMP(#{params.sheyingshiOrderStartTimeEnd})
and UNIX_TIMESTAMP(a.sheyingshi_order_end_time) >= UNIX_TIMESTAMP(#{params.sheyingshiOrderEndTimeStart})
and UNIX_TIMESTAMP(a.sheyingshi_order_end_time) <= UNIX_TIMESTAMP(#{params.sheyingshiOrderEndTimeEnd})
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and sheyingshi.sheyingshi_name like CONCAT('%',#{params.sheyingshiName},'%')
and sheyingshi.sheyingshi_phone like CONCAT('%',#{params.sheyingshiPhone},'%')
and sheyingshi.sheyingshi_id_number like CONCAT('%',#{params.sheyingshiIdNumber},'%')
and sheyingshi.sex_types = #{params.sexTypes}
and sheyingshi.age >= #{params.ageStart}
and sheyingshi.age <= #{params.ageEnd}
and sheyingshi.fengge_types = #{params.fenggeTypes}
and sheyingshi.sheyingshi_xingji_types = #{params.sheyingshiXingjiTypes}
and sheyingshi.sheyingshi_content like CONCAT('%',#{params.sheyingshiContent},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
order by a.${params.orderBy} desc
